                else if (char == 0x5c) output[outputLength++] = "\\";
                else if (char == 0x22) {
                    // solhint-disable-next-line quotes
                    output[outputLength++] = '"';
                }
            } else {
                output[outputLength++] = char;
            }
        }
        // write the actual length and deallocate unused memory
        assembly ("memory-safe") {
            mstore(output, outputLength)
            mstore(0x40, add(output, shl(5, shr(5, add(outputLength, 63)))))
        }

        return string(output);
    }

    /**
     * @dev Reads a bytes32 from a bytes array without bounds checking.
     *
     * NOTE: making this function internal would mean it could be used with memory unsafe offset, and marking the
     * assembly block as such would prevent some optimizations.
     */
    function _unsafeReadBytesOffset(bytes memory buffer, uint256 offset) private pure returns (bytes32 value) {
        // This is not memory safe in the general case, but all calls to this private function are within bounds.
        assembly ("memory-safe") {
            value := mload(add(add(buffer, 0x20), offset))
        }
    }
}

contracts/BTC1USD.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IBTC1USD.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import "@openzeppelin/contracts/access/Ownable2Step.sol";

contract BTC1USD is ERC20, ERC20Permit, Ownable2Step, IBTC1USD {
    address public vault;
    address public weeklyDistribution;
    bool public criticalParamsLocked;

    // Timelock for critical address changes (2-day delay)
    uint256 public constant TIMELOCK_DELAY = 2 days;

    struct PendingChange {
        address newAddress;
        uint256 executeAfter;
    }

    PendingChange public pendingVaultChange;
    PendingChange public pendingWeeklyDistributionChange;

    modifier onlyVaultOrDistribution() {
        require(
            vault != address(0) && weeklyDistribution != address(0),
            "BTC1USD: not configured"
        );
        require(
            msg.sender == vault || msg.sender == weeklyDistribution,
            "BTC1USD: unauthorized"
        );
        _;
    }

    modifier onlyOwnerUnlocked() {
        require(!criticalParamsLocked, "BTC1USD: params locked");
        _;
    }
    modifier onlyVault() {
        require(msg.sender == vault, "BTC1USD: only vault");
        _;
    }

    event VaultChanged(address indexed oldVault, address indexed newVault);
    event WeeklyDistributionChanged(
        address indexed oldDist,
        address indexed newDist
    );
    event CriticalParamsLocked(address owner);

    // Timelock events
    event VaultChangeInitiated(
        address indexed oldVault,
        address indexed newVault,
        uint256 executeAfter
    );
    event VaultChangeCancelled(address indexed cancelledVault);
    event WeeklyDistributionChangeInitiated(
        address indexed oldDist,
        address indexed newDist,
        uint256 executeAfter
    );
    event WeeklyDistributionChangeCancelled(address indexed cancelledDist);

    constructor(
        address initialOwner,
        address _vault,
        address _weeklyDistribution
    ) ERC20("BTC1USD", "BTC1") ERC20Permit("BTC1USD") Ownable(initialOwner) {
        vault = _vault;
        weeklyDistribution = _weeklyDistribution;

        if (_vault != address(0)) {
            emit VaultChanged(address(0), _vault);
        }
        if (_weeklyDistribution != address(0)) {
            emit WeeklyDistributionChanged(address(0), _weeklyDistribution);
        }
    }

    function decimals() public pure override(ERC20, IBTC1USD) returns (uint8) {
        return 8;
    }

    /*//////////////////////////////////////////////////////////////
                    INITIAL SETUP (DEPLOYMENT ONLY)
    //////////////////////////////////////////////////////////////*/

    /// @notice Set vault address (only works if current vault is zero)
    /// @dev For initial deployment only. Use timelock functions for changes.
    function setVault(address _vault) external onlyOwner onlyOwnerUnlocked {
        require(_vault != address(0), "zero vault");
        require(vault == address(0), "vault already set - use timelock");
        vault = _vault;
        emit VaultChanged(address(0), _vault);
    }

    /// @notice Set weekly distribution address (only works if current address is zero)
    /// @dev For initial deployment only. Use timelock functions for changes.
    function setWeeklyDistribution(
        address _wd
    ) external onlyOwner onlyOwnerUnlocked {
        require(_wd != address(0), "zero distribution");
        require(
            weeklyDistribution == address(0),
            "distribution already set - use timelock"
        );
        weeklyDistribution = _wd;
        emit WeeklyDistributionChanged(address(0), _wd);
    }

    /*//////////////////////////////////////////////////////////////
                    TIMELOCK VAULT ADDRESS CHANGES
    //////////////////////////////////////////////////////////////*/

    /// @notice Initiate vault address change (step 1 of 2)
    function initiateVaultChange(
        address newVault
    ) external onlyOwner onlyOwnerUnlocked {
        require(newVault != address(0), "zero vault");
        require(newVault != vault, "same vault");
        require(
            pendingVaultChange.newAddress == address(0),
            "BTC1USD: vault change pending"
        );

        pendingVaultChange = PendingChange({
            newAddress: newVault,
            executeAfter: block.timestamp + TIMELOCK_DELAY
        });

        emit VaultChangeInitiated(
            vault,
            newVault,
            pendingVaultChange.executeAfter
        );
    }

    /// @notice Execute vault address change after timelock (step 2 of 2)
    function executeVaultChange() external onlyOwner onlyOwnerUnlocked {
        require(pendingVaultChange.newAddress != address(0), "no pending");
        require(block.timestamp >= pendingVaultChange.executeAfter, "timelock");
        address oldVault = vault;
        vault = pendingVaultChange.newAddress;

        delete pendingVaultChange;

        emit VaultChanged(oldVault, vault);
    }

    /// @notice Cancel pending vault address change
    function cancelVaultChange() external onlyOwner onlyOwnerUnlocked {
        require(
            pendingVaultChange.newAddress != address(0),
            "no pending change"
        );

        address cancelled = pendingVaultChange.newAddress;
        delete pendingVaultChange;

        emit VaultChangeCancelled(cancelled);
    }

    /*//////////////////////////////////////////////////////////////
                TIMELOCK WEEKLY DISTRIBUTION CHANGES
    //////////////////////////////////////////////////////////////*/

    /// @notice Initiate weekly distribution address change (step 1 of 2)
    function initiateWeeklyDistributionChange(
        address newDist
    ) external onlyOwner onlyOwnerUnlocked {
        require(newDist != address(0), "zero distribution");
        require(newDist != weeklyDistribution, "same distribution");
        require(
            pendingWeeklyDistributionChange.newAddress == address(0),
            "BTC1USD: weekly dist change pending"
        );

        pendingWeeklyDistributionChange = PendingChange({
            newAddress: newDist,
            executeAfter: block.timestamp + TIMELOCK_DELAY
        });

        emit WeeklyDistributionChangeInitiated(
            weeklyDistribution,
            newDist,
            pendingWeeklyDistributionChange.executeAfter
        );
    }

    /// @notice Execute weekly distribution address change after timelock (step 2 of 2)
    function executeWeeklyDistributionChange()
        external
        onlyOwner
        onlyOwnerUnlocked
    {
        require(
            pendingWeeklyDistributionChange.newAddress != address(0),
            "no pending change"
        );
        require(
            block.timestamp >= pendingWeeklyDistributionChange.executeAfter,
            "timelock not expired"
        );

        address oldDist = weeklyDistribution;
        weeklyDistribution = pendingWeeklyDistributionChange.newAddress;

        delete pendingWeeklyDistributionChange;

        emit WeeklyDistributionChanged(oldDist, weeklyDistribution);
    }

    /// @notice Cancel pending weekly distribution address change
    function cancelWeeklyDistributionChange() external onlyOwner onlyOwnerUnlocked {
        require(
            pendingWeeklyDistributionChange.newAddress != address(0),
            "no pending change"
        );

        address cancelled = pendingWeeklyDistributionChange.newAddress;
        delete pendingWeeklyDistributionChange;

        emit WeeklyDistributionChangeCancelled(cancelled);
    }

    function lockCriticalParams() external onlyOwner {
        require(!criticalParamsLocked, "BTC1USD: already locked");
        require(vault != address(0), "BTC1USD: vault not set");
        require(
            weeklyDistribution != address(0),
            "BTC1USD: weekly dist not set"
        );

        // Clear any pending changes to prevent bypass
        delete pendingVaultChange;
        delete pendingWeeklyDistributionChange;

        criticalParamsLocked = true;
        emit CriticalParamsLocked(owner());
    }

    function mint(
        address to,
        uint256 amount
    ) external override onlyVaultOrDistribution {
        _mint(to, amount);
    }

    function burnFrom(
        address from,
        uint256 amount
    ) external override onlyVault {
        _spendAllowance(from, msg.sender, amount);
        _burn(from, amount);
    }
// check this if it feasible 

function burn(uint256 amount) external 
{ _burn(msg.sender, amount); }

}

contracts/ChainlinkBTCOracleUpgradeable..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";
import "./interfaces/IPriceOracle.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @title ChainlinkBTCOracleUpgradeable (Liveness-Safe)
 * @notice Upgradeable Chainlink oracle without deviation or hard bounds
 *
 * SECURITY GUARANTEES:
 * - Stale timestamp protection
 * - Round completeness validation
 * - Minimum round age (anti-flash)
 * - No manual price injection
 *
 * All prices returned in 8 decimals (USD-8)
 *
 * NOTE:
 * - Relies on Chainlink security guarantees
 * - Economic safety handled in Vault (CR + stress logic)
 */
contract ChainlinkBTCOracleUpgradeable is
    Initializable,
    IPriceOracle,
    OwnableUpgradeable
{
    /*//////////////////////////////////////////////////////////////
                                STORAGE
    //////////////////////////////////////////////////////////////*/

    AggregatorV3Interface internal btcPriceFeed;

    mapping(address => AggregatorV3Interface) public collateralFeeds;
    mapping(address => uint8) public collateralDecimals;

    // ---- Safety parameters ----
    uint256 public constant STALE_THRESHOLD = 1200; // 20 minutes

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/

    event PriceUpdated(uint256 newPrice, uint256 timestamp);
    event CollateralFeedUpdated(address indexed token, address indexed feed, uint8 decimals);
    event Initialized(address indexed owner);

    /*//////////////////////////////////////////////////////////////
                                INIT
    //////////////////////////////////////////////////////////////*/

    function initialize(
        address initialOwner,
        address btcFeed
    ) external initializer {
        require(initialOwner != address(0), "oracle: zero owner");
        require(btcFeed != address(0), "oracle: zero feed");

        __Ownable_init(initialOwner);
        btcPriceFeed = AggregatorV3Interface(btcFeed);

        emit Initialized(initialOwner);
    }

    /*//////////////////////////////////////////////////////////////
                                ADMIN
    //////////////////////////////////////////////////////////////*/

    function setBTCPriceFeed(address feed) external onlyOwner {
        require(feed != address(0), "oracle: zero feed");
        btcPriceFeed = AggregatorV3Interface(feed);
    }

    function setCollateralFeed(
        address token,
        address feed,
        uint8 decimals_
    ) external onlyOwner {
        require(token != address(0), "oracle: zero token");
        require(feed != address(0), "oracle: zero feed");

        collateralFeeds[token] = AggregatorV3Interface(feed);
        collateralDecimals[token] = decimals_;

        emit CollateralFeedUpdated(token, feed, decimals_);
    }

    /*//////////////////////////////////////////////////////////////
                            INTERNAL CORE
    //////////////////////////////////////////////////////////////*/

    function _readFeed(
        AggregatorV3Interface feed,
        uint8 feedDecimals
    ) internal view returns (uint256) {
        (
            uint80 roundID,
            int256 answer,
            ,
            uint256 updatedAt,
            uint80 answeredInRound
        ) = feed.latestRoundData();

        require(answer > 0, "oracle: bad price");
        require(updatedAt > 0, "oracle: no timestamp");
        require(answeredInRound >= roundID, "oracle: stale round");
        require(block.timestamp - updatedAt <= STALE_THRESHOLD, "oracle: stale price");

        uint256 price = uint256(answer);

        // Normalize to 8 decimals
        if (feedDecimals < 8) {
            price *= 10 ** (8 - feedDecimals);
        } else if (feedDecimals > 8) {
            price /= 10 ** (feedDecimals - 8);
        }

        return price;
    }

    /*//////////////////////////////////////////////////////////////
                            PRICE INTERFACE
    //////////////////////////////////////////////////////////////*/

    function getBTCPrice() external view override returns (uint256) {
        return _readFeed(
            btcPriceFeed,
            btcPriceFeed.decimals()
        );
    }

    function getPrice(address token) external view override returns (uint256) {
        if (token != address(0) && address(collateralFeeds[token]) != address(0)) {
            return _readFeed(
                collateralFeeds[token],
                collateralDecimals[token]
            );
        }

        return _readFeed(
            btcPriceFeed,
            btcPriceFeed.decimals()
        );
    }

    function getLastUpdate() external view override returns (uint256) {
        (, , , uint256 ts, ) = btcPriceFeed.latestRoundData();
        return ts;
    }

    function isStale() external view override returns (bool) {
        (, , , uint256 ts, ) = btcPriceFeed.latestRoundData();
        return block.timestamp - ts > STALE_THRESHOLD;
    }

    function decimals() external pure returns (uint8) {
        return 8;
    }

    /*//////////////////////////////////////////////////////////////
                        COMPATIBILITY FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    function updatePrice() external override onlyOwner {
        emit PriceUpdated(this.getBTCPrice(), block.timestamp);
    }

    function updatePrice(uint256) external override onlyOwner {
        revert("oracle: manual price updates disabled");
    }

    function getPriceFeedAddress() external view returns (address) {
        return address(btcPriceFeed);
    }
}

contracts/DAOUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IBTC1USD.sol";
import "./interfaces/IProtocolGovernance.sol";
import "./libraries/SafeMath.sol";

/**
 * @title DAOUpgradeable
 * @notice Upgradeable DAO governance contract
 * 
 * UPGRADEABLE DESIGN:
 * - No constructor (uses initialize function)
 * - Storage layout preserved for future upgrades
 * - Compatible with transparent proxy pattern
 * 
 * STORAGE LAYOUT - DO NOT REORDER:
 * - All state variables below MUST remain in this exact order
 * - New state variables can ONLY be added at the end
 */
contract DAOUpgradeable {
    using SafeMath for uint256;

    // Core contracts
    IBTC1USD public btc1usd;
    IProtocolGovernance public protocolGovernance;
    
    // DAO configuration
    uint256 public constant MIN_PROPOSAL_THRESHOLD = 10000e8; // 10,000 BTC1USD (8 decimals)
    uint256 public constant MIN_VOTING_PERIOD = 3 days;
    uint256 public constant MAX_VOTING_PERIOD = 14 days;
    uint256 public constant QUORUM_PERCENTAGE = 4; // 4% of total supply
    uint256 public constant PROPOSAL_THRESHOLD_PERCENTAGE = 1; // 1% of total supply
    
    // Proposal states
    enum ProposalState {
        Pending,
        Active,
        Canceled,
        Defeated,
        Succeeded,
        Queued,
        Expired,
        Executed
    }
    
    // Proposal types
    enum ProposalType {
        ParameterChange,
        EmergencyAction,
        ContractUpgrade,
        TreasuryAction,
        GovernanceChange
    }
    
    // Proposal structure
    struct Proposal {
        uint256 id;
        address proposer;
        string title;
        string description;
        uint256 startBlock;
        uint256 endBlock;
        uint256 forVotes;
        uint256 againstVotes;
        uint256 abstainVotes;
        mapping(address => Receipt) receipts;
        ProposalState state;
        ProposalType proposalType;
        bytes32 targets;
        uint256[] values;
        string[] signatures;
        bytes[] calldatas;
        uint256 eta;
        bool canceled;
        bool executed;
    }
    
    // Receipt structure for tracking votes
    struct Receipt {
        bool hasVoted;
        uint8 support;
        uint256 votes;
    }
    
    // Delegate structure
    struct Delegate {
        uint256 balance;
        address delegate;
        uint256 delegatedVotes;
    }
    
    // Storage
    mapping(uint256 => Proposal) public proposals;
    mapping(address => mapping(uint256 => uint256)) public proposalVoteCounts;
    mapping(address => Delegate) public delegates;
    
    uint256 public proposalCount;
    uint256 public totalSupply;
    
    // Upgradeable-specific
    bool private _initialized;
    
    // Events
    event Initialized(address indexed btc1usd, address indexed protocolGovernance);
    
    event ProposalCreated(
        uint256 indexed proposalId,
        address indexed proposer,
        string title,
        string description,
        uint256 startBlock,
        uint256 endBlock,
        ProposalType proposalType
    );
    
    event VoteCast(
        address indexed voter,
        uint256 indexed proposalId,
        uint8 support,
        uint256 votes,
        string reason
    );
    
    event ProposalCanceled(uint256 indexed proposalId);
    event ProposalExecuted(uint256 indexed proposalId);
    event ProposalQueued(uint256 indexed proposalId, uint256 eta);
    event DelegateChanged(address indexed delegator, address indexed fromDelegate, address indexed toDelegate);
    event DelegateVotesChanged(address indexed delegate, uint256 previousBalance, uint256 newBalance);
    
    modifier onlyDAO() {
        require(msg.sender == address(this), "DAO: caller is not DAO");
        _;
    }
    
    function initialize(address _btc1usd, address _protocolGovernance) external {
        require(!_initialized, "DAO: already initialized");
        require(_btc1usd != address(0), "DAO: btc1usd is zero address");
        require(_protocolGovernance != address(0), "DAO: protocolGovernance is zero address");
        
        btc1usd = IBTC1USD(_btc1usd);
        protocolGovernance = IProtocolGovernance(_protocolGovernance);
        totalSupply = btc1usd.totalSupply();
        
        _initialized = true;
        emit Initialized(_btc1usd, _protocolGovernance);
    }
    
    // Proposal creation
    function propose(
        string memory title,
        string memory description,
        address[] memory targets,
        uint256[] memory values,
        string[] memory signatures,
        bytes[] memory calldatas,
        ProposalType proposalType
    ) external returns (uint256) {
        require(btc1usd.balanceOf(msg.sender) >= MIN_PROPOSAL_THRESHOLD,
            "DAO: proposer must hold minimum tokens");

        uint256 startBlock = block.number; // Start immediately
        uint256 endBlock = startBlock + MIN_VOTING_PERIOD;
        
        proposalCount++;
        Proposal storage newProposal = proposals[proposalCount];
        
        newProposal.id = proposalCount;
        newProposal.proposer = msg.sender;
        newProposal.title = title;
        newProposal.description = description;
        newProposal.startBlock = startBlock;
        newProposal.endBlock = endBlock;
        newProposal.state = ProposalState.Pending;
        newProposal.proposalType = proposalType;
        
        emit ProposalCreated(
            proposalCount,
            msg.sender,
            title,
            description,
            startBlock,
            endBlock,
            proposalType
        );
        
        return proposalCount;
    }
    
    // Voting functions
    function castVote(uint256 proposalId, uint8 support) external {
        castVoteInternal(msg.sender, proposalId, support, "");
    }
    
    function castVoteWithReason(
        uint256 proposalId,
        uint8 support,
        string memory reason
    ) external {
        castVoteInternal(msg.sender, proposalId, support, reason);
    }
    
    function castVoteBySig(
        uint256 proposalId,
        uint8 support,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external {
        // Implementation for signature-based voting
        bytes32 domainSeparator = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("DAO")),
                getChainId(),
                address(this)
            )
        );
        
        bytes32 structHash = keccak256(
            abi.encode(
                keccak256("CastVote(uint256 proposalId,uint8 support)"),
                proposalId,
                support
            )
        );
        
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
        
        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "DAO: invalid signature");
        
        castVoteInternal(signatory, proposalId, support, "");
    }
    
    function castVoteInternal(
        address voter,
        uint256 proposalId,
        uint8 support,
        string memory reason
    ) internal {
        require(state(proposalId) == ProposalState.Active, "DAO: voting is closed");
        require(support <= 2, "DAO: invalid vote type");
        
        Proposal storage proposal = proposals[proposalId];
        
        uint256 votes = getVotes(voter);
        require(votes > 0, "DAO: voter has no votes");
        
        Receipt storage receipt = proposal.receipts[voter];
        require(!receipt.hasVoted, "DAO: voter already voted");
        
        receipt.hasVoted = true;
        receipt.support = support;
        receipt.votes = votes;
        
        if (support == 0) {
            proposal.againstVotes = proposal.againstVotes.add(votes);
        } else if (support == 1) {
            proposal.forVotes = proposal.forVotes.add(votes);
        } else if (support == 2) {
            proposal.abstainVotes = proposal.abstainVotes.add(votes);
        }
        
        emit VoteCast(voter, proposalId, support, votes, reason);
    }
    
    // Proposal state functions
    function queue(uint256 proposalId) external {
        require(state(proposalId) == ProposalState.Succeeded, "DAO: proposal not successful");
        
        Proposal storage proposal = proposals[proposalId];
        uint256 eta = block.timestamp + 2 days; // 2 day timelock
        proposal.eta = eta;
        proposal.state = ProposalState.Queued;
        
        emit ProposalQueued(proposalId, eta);
    }
    
    function execute(uint256 proposalId) external payable {
        require(state(proposalId) == ProposalState.Queued, "DAO: proposal not queued");
        require(block.timestamp >= proposals[proposalId].eta, "DAO: timelock not expired");
        
        Proposal storage proposal = proposals[proposalId];
        proposal.executed = true;
        proposal.state = ProposalState.Executed;
        
        // Execute the proposal actions
        // This would typically involve calling the target contracts with the provided calldata
        
        emit ProposalExecuted(proposalId);
    }
    
    function cancel(uint256 proposalId) external {
        ProposalState currentState = state(proposalId);
        require(
            currentState == ProposalState.Pending ||
            currentState == ProposalState.Active ||
            currentState == ProposalState.Queued,
            "DAO: proposal not active"
        );
        
        Proposal storage proposal = proposals[proposalId];
        proposal.canceled = true;
        proposal.state = ProposalState.Canceled;
        
        emit ProposalCanceled(proposalId);
    }
    
    // Delegation functions
    function delegate(address delegatee) external {
        delegateInternal(msg.sender, delegatee);
    }
    
    function delegateBySig(
        address delegatee,
        uint256 nonce,
        uint256 expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external {
        // Implementation for signature-based delegation
        bytes32 domainSeparator = keccak256(
            abi.encode(
                keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)"),
                keccak256(bytes("DAO")),
                getChainId(),
                address(this)
            )
        );
        
        bytes32 structHash = keccak256(
            abi.encode(
                keccak256("Delegate(address delegatee,uint256 nonce,uint256 expiry)"),
                delegatee,
                nonce,
                expiry
            )
        );
        
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
        
        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "DAO: invalid signature");
        
        delegateInternal(signatory, delegatee);
    }
    
    function delegateInternal(address delegator, address delegatee) internal {
        require(delegatee != address(0), "DAO: delegatee is zero address");
        
        Delegate storage delegatorDelegate = delegates[delegator];
        address previousDelegate = delegatorDelegate.delegate;
        
        delegatorDelegate.delegate = delegatee;
        
        // Transfer voting power
        uint256 delegatorBalance = btc1usd.balanceOf(delegator);
        _moveDelegates(previousDelegate, delegatee, delegatorBalance);
        
        emit DelegateChanged(delegator, previousDelegate, delegatee);
    }
    
    // Helper functions
    function state(uint256 proposalId) public view returns (ProposalState) {
        require(proposalId <= proposalCount, "DAO: invalid proposal id");
        require(proposalId > 0, "DAO: invalid proposal id");

        Proposal storage proposal = proposals[proposalId];

        if (proposal.canceled) {
            return ProposalState.Canceled;
        } else if (block.number < proposal.startBlock) {
            return ProposalState.Pending;
        } else if (block.number <= proposal.endBlock) {
            return ProposalState.Active;
        } else if (proposal.executed) {
            return ProposalState.Executed;
        } else if (proposal.eta > 0 && block.timestamp >= proposal.eta + 2 weeks) {
            return ProposalState.Expired;
        } else if (proposal.eta > 0) {
            return ProposalState.Queued;
        } else {
            // Voting has ended, check if proposal succeeded or was defeated
            uint256 quorum = quorumVotes();
            uint256 totalVotes = proposal.forVotes.add(proposal.againstVotes).add(proposal.abstainVotes);

            // Check quorum and majority
            if (totalVotes < quorum || proposal.forVotes <= proposal.againstVotes) {
                return ProposalState.Defeated;
            } else {
                return ProposalState.Succeeded;
            }
        }
    }
    
    function getVotes(address account) public view returns (uint256) {
        // If user has delegated, return delegated votes, otherwise return their token balance
        uint256 delegatedVotes = delegates[account].delegatedVotes;
        if (delegatedVotes > 0) {
            return delegatedVotes;
        }
        // For users who haven't delegated, use their token balance directly
        return btc1usd.balanceOf(account);
    }
    
    function getChainId() internal view returns (uint256) {
        uint256 chainId;
        assembly {
            chainId := chainid()
        }
        return chainId;
    }
    
    function _moveDelegates(address srcRep, address dstRep, uint256 amount) internal {
        if (srcRep != dstRep && amount > 0) {
            if (srcRep != address(0)) {
                uint256 srcRepNum = delegates[srcRep].delegatedVotes;
                delegates[srcRep].delegatedVotes = srcRepNum.sub(amount);
                emit DelegateVotesChanged(srcRep, srcRepNum, srcRepNum.sub(amount));
            }
            
            if (dstRep != address(0)) {
                uint256 dstRepNum = delegates[dstRep].delegatedVotes;
                delegates[dstRep].delegatedVotes = dstRepNum.add(amount);
                emit DelegateVotesChanged(dstRep, dstRepNum, dstRepNum.add(amount));
            }
        }
    }

    // NEW: Allow upgrading contract references
    function setBTC1USD(address _btc1usd) external onlyDAO {
        require(_btc1usd != address(0), "DAO: btc1usd is zero address");
        btc1usd = IBTC1USD(_btc1usd);
    }

    function setProtocolGovernance(address _protocolGovernance) external onlyDAO {
        require(_protocolGovernance != address(0), "DAO: protocolGovernance is zero address");
        protocolGovernance = IProtocolGovernance(_protocolGovernance);
    }
    
    // View functions
    function quorumVotes() public view returns (uint256) {
        return totalSupply.mul(QUORUM_PERCENTAGE).div(100);
    }
    
    function proposalThreshold() public view returns (uint256) {
        return totalSupply.mul(PROPOSAL_THRESHOLD_PERCENTAGE).div(100);
    }
    
    function fetchProposalVotes(uint256 proposalId) external view returns (uint256 againstVotes, uint256 forVotes, uint256 abstainVotes) {
        Proposal storage proposal = proposals[proposalId];
        return (proposal.againstVotes, proposal.forVotes, proposal.abstainVotes);
    }

    function getProposal(uint256 proposalId) external view returns (
        address proposer,
        string memory title,
        string memory description,
        uint8 category,
        uint256 forVotes,
        uint256 againstVotes,
        uint256 abstainVotes,
        uint256 startBlock,
        uint256 endBlock,
        uint256 eta,
        bool executed,
        bool canceled
    ) {
        Proposal storage proposal = proposals[proposalId];
        return (
            proposal.proposer,
            proposal.title,
            proposal.description,
            uint8(proposal.proposalType),
            proposal.forVotes,
            proposal.againstVotes,
            proposal.abstainVotes,
            proposal.startBlock,
            proposal.endBlock,
            proposal.eta,
            proposal.executed,
            proposal.canceled
        );
    }

    function getQuorum(uint8 /* category */) external view returns (uint256) {
        // Return quorum based on total supply (4% of total supply)
        return quorumVotes();
    }

    function getVotingPower(address account) external view returns (uint256) {
        return getVotes(account);
    }

    function getReceipt(uint256 proposalId, address voter) external view returns (
        bool hasVoted,
        uint8 support,
        uint256 votes,
        string memory reason
    ) {
        Proposal storage proposal = proposals[proposalId];
        Receipt storage receipt = proposal.receipts[voter];
        return (receipt.hasVoted, receipt.support, receipt.votes, "");
    }

    function canVote(uint256 proposalId, address voter) external view returns (bool) {
        // Check if proposal is active
        if (state(proposalId) != ProposalState.Active) {
            return false;
        }

        // Check if user has voting power
        if (getVotes(voter) == 0) {
            return false;
        }

        // Check if user has already voted
        Proposal storage proposal = proposals[proposalId];
        if (proposal.receipts[voter].hasVoted) {
            return false;
        }

        return true;
    }

    function isInitialized() external view returns (bool) {
        return _initialized;
    }
}

