contracts/DevWalletUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title DevWalletUpgradeable
 * @notice Upgradeable version of DevWallet
 * 
 * UPGRADEABLE DESIGN:
 * - No constructor (uses initialize function)
 * - Storage layout preserved for future upgrades
 * - Compatible with transparent proxy pattern
 */
contract DevWalletUpgradeable is ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct WalletInfo {
        string name;
        string description;
        bool isActive;
    }

    mapping(address => WalletInfo) public walletInfos;
    address[] public walletAddresses;

    struct DistributionStats {
        uint256 totalDistributions;
        uint256 totalAmountDistributed;
        uint256 totalRecipients;
        uint256 totalFailed;
    }

    mapping(address => DistributionStats) public distributionStats;
    uint256 public totalDistributionCount;

    address public owner;
    bool private _initialized;

    event IndividualTransfer(address indexed token, address indexed to, uint256 amount);
    event TransferFailed(address indexed token, address indexed to, uint256 amount);
    event BatchTransferCompleted(address indexed token, uint256 totalRecipients, uint256 totalSent, uint256 totalFailed);
    event WalletAdded(address indexed wallet, string name);
    event WalletUpdated(address indexed wallet, string name);
    event WalletRemoved(address indexed wallet);
    event WalletActivated(address indexed wallet);
    event WalletDeactivated(address indexed wallet);
    event Initialized(address indexed owner);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyOwner() {
        require(msg.sender == owner, "DevWallet: caller is not owner");
        _;
    }

    function initialize(address _owner) external {
        require(!_initialized, "DevWallet: already initialized");
        require(_owner != address(0), "DevWallet: owner is zero address");
        
        owner = _owner;
        _initialized = true;
        
        emit Initialized(_owner);
    }

    function addWallet(address wallet, string memory name, string memory description) external onlyOwner {
        require(wallet != address(0), "DevWallet: invalid wallet address");
        require(bytes(name).length > 0, "DevWallet: name cannot be empty");
        require(!walletInfos[wallet].isActive, "DevWallet: wallet already exists");

        walletInfos[wallet] = WalletInfo({name: name, description: description, isActive: true});
        walletAddresses.push(wallet);
        emit WalletAdded(wallet, name);
    }

    function updateWallet(address wallet, string memory name, string memory description) external onlyOwner {
        require(wallet != address(0), "DevWallet: invalid wallet address");
        require(bytes(name).length > 0, "DevWallet: name cannot be empty");
        require(walletInfos[wallet].isActive, "DevWallet: wallet does not exist");
        
        walletInfos[wallet].name = name;
        walletInfos[wallet].description = description;
        emit WalletUpdated(wallet, name);
    }

    function removeWallet(address wallet) external onlyOwner {
        require(wallet != address(0), "DevWallet: invalid wallet address");
        require(walletInfos[wallet].isActive, "DevWallet: wallet does not exist");
        
        delete walletInfos[wallet];
        
        for (uint256 i = 0; i < walletAddresses.length; i++) {
            if (walletAddresses[i] == wallet) {
                walletAddresses[i] = walletAddresses[walletAddresses.length - 1];
                walletAddresses.pop();
                break;
            }
        }
        emit WalletRemoved(wallet);
    }

    function activateWallet(address wallet) external onlyOwner {
        require(wallet != address(0), "DevWallet: invalid wallet address");
        require(walletInfos[wallet].isActive == false, "DevWallet: wallet already active");
        walletInfos[wallet].isActive = true;
        emit WalletActivated(wallet);
    }

    function deactivateWallet(address wallet) external onlyOwner {
        require(wallet != address(0), "DevWallet: invalid wallet address");
        require(walletInfos[wallet].isActive, "DevWallet: wallet not active");
        walletInfos[wallet].isActive = false;
        emit WalletDeactivated(wallet);
    }

    function batchTransfer(IERC20 token, address[] calldata recipients, uint256[] calldata amounts) external nonReentrant onlyOwner {
        require(recipients.length == amounts.length, "length mismatch");
        require(recipients.length > 0, "no recipients");

        uint256 totalSent = 0;
        uint256 totalFailed = 0;

        for (uint256 i = 0; i < recipients.length; ++i) {
            address to = recipients[i];
            uint256 amount = amounts[i];

            (bool success, bytes memory data) = address(token).call(
                abi.encodeWithSelector(token.transfer.selector, to, amount)
            );

            if (success && (data.length == 0 || abi.decode(data, (bool)))) {
                emit IndividualTransfer(address(token), to, amount);
                totalSent += amount;
            } else {
                emit TransferFailed(address(token), to, amount);
                totalFailed++;
            }
        }

        address tokenAddress = address(token);
        DistributionStats storage stats = distributionStats[tokenAddress];
        stats.totalDistributions++;
        stats.totalAmountDistributed += totalSent;
        stats.totalRecipients += recipients.length;
        stats.totalFailed += totalFailed;
        totalDistributionCount++;

        emit BatchTransferCompleted(tokenAddress, recipients.length, totalSent, totalFailed);
    }

    function getWalletAddresses() external view returns (address[] memory) {
        return walletAddresses;
    }
    
    function getWalletInfo(address wallet) external view returns (string memory name, string memory description, bool isActive) {
        WalletInfo memory info = walletInfos[wallet];
        return (info.name, info.description, info.isActive);
    }

    function getDistributionStats(address token) external view returns (uint256 totalDistributions, uint256 totalAmountDistributed, uint256 totalRecipients, uint256 totalFailed) {
        DistributionStats memory stats = distributionStats[token];
        return (stats.totalDistributions, stats.totalAmountDistributed, stats.totalRecipients, stats.totalFailed);
    }

    function getTotalDistributionCount() external view returns (uint256) {
        return totalDistributionCount;
    }

    function withdrawToken(IERC20 token, address to, uint256 amount) external onlyOwner {
        token.safeTransfer(to, amount);
    }

    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "DevWallet: new owner is zero address");
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    function isInitialized() external view returns (bool) {
        return _initialized;
    }
}

contracts/EndowmentManagerUpgradeable.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IBTC1USD.sol";
import "./libraries/SafeMath.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @title EndowmentManagerUpgradeable
 * @notice Upgradeable endowment manager for non-profit distributions
 *
 * UPGRADEABLE DESIGN:
 * - Uses OpenZeppelin upgradeable patterns
 * - No constructor (uses initialize function)
 * - Storage layout preserved for future upgrades
 */
contract EndowmentManagerUpgradeable is Initializable, ReentrancyGuard, OwnableUpgradeable {
    using SafeMath for uint256;

    IBTC1USD public btc1usd;

    address public governanceDAO;
    address public endowmentWallet;
    
    enum NonProfitCategory {
        Humanitarian,
        Zakat,
        Development,
        Poverty,
        Education,
        Healthcare,
        Environment
    }

    struct NonProfit {
        string name;
        address wallet;
        bool approved;
        uint256 totalReceived;
        string description;
        string website;
        NonProfitCategory category;
        uint256 addedTimestamp;
        bool verified;
        uint256 allocationWeight;
    }

    mapping(address => NonProfit) public nonProfits;
    address[] public approvedNonProfits;

    mapping(NonProfitCategory => uint256) public categoryCount;

    struct NonProfitProposal {
        address proposer;
        address wallet;
        string name;
        string description;
        string website;
        NonProfitCategory category;
        uint256 votesFor;
        uint256 votesAgainst;
        bool executed;
        bool approved;
        uint256 proposalTimestamp;
        uint256 votingDeadline;
    }

    mapping(uint256 => NonProfitProposal) public proposals;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    uint256 public proposalCount;
    uint256 public constant VOTING_PERIOD = 7 days;

    uint256 public constant PROPOSAL_THRESHOLD = 1000 * 10**8;

    uint256 public totalEndowmentBalance;
    uint256 public lastDistributionTime;
    uint256 public constant DISTRIBUTION_INTERVAL = 7 hours;
    
    struct MonthlyDistribution {
        uint256 timestamp;
        uint256 totalAmount;
        uint256 recipientCount;
        mapping(address => uint256) allocations;
    }
    
    mapping(uint256 => MonthlyDistribution) public monthlyDistributions;
    uint256 public distributionCount;
    
    event NonProfitAdded(address indexed wallet, string name, NonProfitCategory category);
    event NonProfitRemoved(address indexed wallet);
    event NonProfitVerified(address indexed wallet, bool verified);
    event NonProfitWeightUpdated(address indexed wallet, uint256 newWeight);
    event MonthlyDistributionExecuted(uint256 indexed distributionId, uint256 totalAmount, uint256 recipientCount);
    event FundsAllocated(address indexed recipient, uint256 amount, uint256 distributionId);
    event ProposalCreated(uint256 indexed proposalId, address indexed proposer, address wallet, string name);
    event ProposalVoted(uint256 indexed proposalId, address indexed voter, bool support, uint256 votingPower);
    event ProposalExecuted(uint256 indexed proposalId, bool approved);
    event Initialized(address indexed owner);

    modifier onlyOwnerOrDAO() {
        require(
            msg.sender == owner() || msg.sender == governanceDAO,
            "EndowmentManager: caller is not admin or DAO"
        );
        _;
    }

    modifier onlyDAO() {
        require(msg.sender == governanceDAO, "EndowmentManager: caller is not DAO");
        _;
    }

    modifier onlyEndowmentWallet() {
        require(msg.sender == endowmentWallet, "EndowmentManager: caller is not endowment wallet");
        _;
    }
    
    /**
     * @dev Initialize function replaces constructor for upgradeable pattern
     */
    function initialize(address initialOwner, address _btc1usd, address _endowmentWallet) external initializer {
        require(initialOwner != address(0), "EndowmentManager: owner is zero address");
        require(_btc1usd != address(0), "EndowmentManager: btc1usd is zero address");
        require(_endowmentWallet != address(0), "EndowmentManager: endowment wallet is zero address");
        
        __Ownable_init(initialOwner);
        btc1usd = IBTC1USD(_btc1usd);
        endowmentWallet = _endowmentWallet;
        lastDistributionTime = block.timestamp;
        
        emit Initialized(initialOwner);
    }
    
    function setGovernanceDAO(address _governanceDAO) external onlyOwner {
        require(_governanceDAO != address(0), "EndowmentManager: DAO is zero address");
        governanceDAO = _governanceDAO;
    }

    function addNonProfit(
        address wallet,
        string memory name,
        string memory description,
        string memory website,
        NonProfitCategory category
    ) external onlyOwnerOrDAO {
        require(wallet != address(0), "EndowmentManager: invalid wallet address");
        require(!nonProfits[wallet].approved, "EndowmentManager: non-profit already exists");

        nonProfits[wallet] = NonProfit({
            name: name,
            wallet: wallet,
            approved: true,
            totalReceived: 0,
            description: description,
            website: website,
            category: category,
            addedTimestamp: block.timestamp,
            verified: false,
            allocationWeight: 100
        });

        approvedNonProfits.push(wallet);
        categoryCount[category]++;
        emit NonProfitAdded(wallet, name, category);
    }
    
    function removeNonProfit(address wallet) external onlyOwner {
        require(nonProfits[wallet].approved, "EndowmentManager: non-profit not found");

        NonProfitCategory category = nonProfits[wallet].category;
        nonProfits[wallet].approved = false;
        categoryCount[category]--;

        for (uint i = 0; i < approvedNonProfits.length; i++) {
            if (approvedNonProfits[i] == wallet) {
                approvedNonProfits[i] = approvedNonProfits[approvedNonProfits.length - 1];
                approvedNonProfits.pop();
                break;
            }
        }

        emit NonProfitRemoved(wallet);
    }

    function setNonProfitVerified(address wallet, bool verified) external onlyOwner {
        require(nonProfits[wallet].approved, "EndowmentManager: non-profit not found");
        nonProfits[wallet].verified = verified;
        emit NonProfitVerified(wallet, verified);
    }

    function setNonProfitWeight(address wallet, uint256 weight) external onlyOwner {
        require(nonProfits[wallet].approved, "EndowmentManager: non-profit not found");
        require(weight > 0 && weight <= 500, "EndowmentManager: weight must be between 1 and 500");
        nonProfits[wallet].allocationWeight = weight;
        emit NonProfitWeightUpdated(wallet, weight);
    }
    
    function canDistribute() public view returns (bool) {
        return block.timestamp >= lastDistributionTime.add(DISTRIBUTION_INTERVAL) && 
               approvedNonProfits.length > 0 &&
               btc1usd.balanceOf(endowmentWallet) > 0;
    }
    
    function executeMonthlyDistribution() external onlyOwnerOrDAO nonReentrant {
        require(canDistribute(), "EndowmentManager: cannot distribute now");

        uint256 totalBalance = btc1usd.balanceOf(endowmentWallet);
        require(totalBalance > 0, "EndowmentManager: no funds to distribute");

        uint256 recipientCount = approvedNonProfits.length;

        uint256 totalWeight = 0;
        for (uint i = 0; i < approvedNonProfits.length; i++) {
            totalWeight = totalWeight.add(nonProfits[approvedNonProfits[i]].allocationWeight);
        }

        distributionCount++;
        MonthlyDistribution storage distribution = monthlyDistributions[distributionCount];
        distribution.timestamp = block.timestamp;
        distribution.totalAmount = totalBalance;
        distribution.recipientCount = recipientCount;

        for (uint i = 0; i < approvedNonProfits.length; i++) {
            address recipient = approvedNonProfits[i];
            uint256 weight = nonProfits[recipient].allocationWeight;

            uint256 allocation = totalBalance.mul(weight).div(totalWeight);

            btc1usd.transferFrom(endowmentWallet, recipient, allocation);

            nonProfits[recipient].totalReceived = nonProfits[recipient].totalReceived.add(allocation);
            distribution.allocations[recipient] = allocation;

            emit FundsAllocated(recipient, allocation, distributionCount);
        }

        lastDistributionTime = block.timestamp;
        emit MonthlyDistributionExecuted(distributionCount, totalBalance, recipientCount);
    }

    function proposeNonProfit(
        address wallet,
        string memory name,
        string memory description,
        string memory website,
        NonProfitCategory category
    ) external returns (uint256) {
        require(btc1usd.balanceOf(msg.sender) >= PROPOSAL_THRESHOLD, "EndowmentManager: insufficient tokens to propose");
        require(wallet != address(0), "EndowmentManager: invalid wallet address");
        require(!nonProfits[wallet].approved, "EndowmentManager: non-profit already exists");

        proposalCount++;
        proposals[proposalCount] = NonProfitProposal({
            proposer: msg.sender,
            wallet: wallet,
            name: name,
            description: description,
            website: website,
            category: category,
            votesFor: 0,
            votesAgainst: 0,
            executed: false,
            approved: false,
            proposalTimestamp: block.timestamp,
            votingDeadline: block.timestamp.add(VOTING_PERIOD)
        });

        emit ProposalCreated(proposalCount, msg.sender, wallet, name);
        return proposalCount;
    }

    function voteOnProposal(uint256 proposalId, bool support) external {
        require(proposalId <= proposalCount && proposalId > 0, "EndowmentManager: invalid proposal");
        NonProfitProposal storage proposal = proposals[proposalId];
        require(block.timestamp <= proposal.votingDeadline, "EndowmentManager: voting period ended");
        require(!proposal.executed, "EndowmentManager: proposal already executed");
        require(!hasVoted[proposalId][msg.sender], "EndowmentManager: already voted");

        uint256 votingPower = btc1usd.balanceOf(msg.sender);
        require(votingPower > 0, "EndowmentManager: no voting power");

        hasVoted[proposalId][msg.sender] = true;

        if (support) {
            proposal.votesFor = proposal.votesFor.add(votingPower);
        } else {
            proposal.votesAgainst = proposal.votesAgainst.add(votingPower);
        }

        emit ProposalVoted(proposalId, msg.sender, support, votingPower);
    }

    function executeProposal(uint256 proposalId) external {
        require(proposalId <= proposalCount && proposalId > 0, "EndowmentManager: invalid proposal");
        NonProfitProposal storage proposal = proposals[proposalId];
        require(block.timestamp > proposal.votingDeadline, "EndowmentManager: voting period not ended");
        require(!proposal.executed, "EndowmentManager: proposal already executed");

        proposal.executed = true;

        if (proposal.votesFor > proposal.votesAgainst) {
            proposal.approved = true;

            nonProfits[proposal.wallet] = NonProfit({
                name: proposal.name,
                wallet: proposal.wallet,
                approved: true,
                totalReceived: 0,
                description: proposal.description,
                website: proposal.website,
                category: proposal.category,
                addedTimestamp: block.timestamp,
                verified: false,
                allocationWeight: 100
            });

            approvedNonProfits.push(proposal.wallet);
            categoryCount[proposal.category]++;
            emit NonProfitAdded(proposal.wallet, proposal.name, proposal.category);
        }

        emit ProposalExecuted(proposalId, proposal.approved);
    }
    
    function getApprovedNonProfits() external view returns (address[] memory) {
        return approvedNonProfits;
    }
    
    function getNonProfitInfo(address wallet) external view returns (
        string memory name,
        bool approved,
        uint256 totalReceived,
        string memory description,
        string memory website,
        NonProfitCategory category,
        uint256 addedTimestamp,
        bool verified,
        uint256 allocationWeight
    ) {
        NonProfit memory np = nonProfits[wallet];
        return (
            np.name,
            np.approved,
            np.totalReceived,
            np.description,
            np.website,
            np.category,
            np.addedTimestamp,
            np.verified,
            np.allocationWeight
        );
    }

    function getDistributionAllocation(uint256 distributionId, address recipient) external view returns (uint256) {
        return monthlyDistributions[distributionId].allocations[recipient];
    }
    
    function getNextDistributionTime() external view returns (uint256) {
        return lastDistributionTime.add(DISTRIBUTION_INTERVAL);
    }
    
    function getCurrentEndowmentBalance() external view returns (uint256) {
        return btc1usd.balanceOf(endowmentWallet);
    }
    
    function setEndowmentWallet(address _endowmentWallet) external onlyOwner {
        require(_endowmentWallet != address(0), "EndowmentManager: endowment wallet is zero address");
        endowmentWallet = _endowmentWallet;
    }


    function setBTC1USD(address _btc1usd) external onlyOwner {
        require(_btc1usd != address(0), "EndowmentManager: btc1usd is zero address");
        btc1usd = IBTC1USD(_btc1usd);
    }
}

