contracts/ProtocolGovernanceUpgradeable..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IBTC1USD.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IWeeklyDistribution.sol";
import "./interfaces/IEndowmentManager.sol";
import "./interfaces/IPriceOracle.sol";
import "./libraries/SafeMath.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

/**
 * @title ProtocolGovernanceUpgradeable
 * @notice Upgradeable protocol governance contract
 *
 * UPGRADEABLE DESIGN:
 * - Uses OpenZeppelin upgradeable patterns
 * - No constructor (uses initialize function)
 * - Storage layout preserved for future upgrades
 * 
 * STORAGE LAYOUT - DO NOT REORDER:
 * - All state variables below MUST remain in this exact order
 * - New state variables can ONLY be added at the end
 */
contract ProtocolGovernanceUpgradeable is Initializable, OwnableUpgradeable {
    using SafeMath for uint256;

    // Core contracts
    IBTC1USD public btc1usd;
    IVault public vault;
    IWeeklyDistribution public weeklyDistribution;
    IEndowmentManager public endowmentManager;
    IPriceOracle public priceOracle;
    
    // Governance
    address public admin;
    address public pendingAdmin;
    address public governanceDAO; // DAO contract address
    uint256 public constant ADMIN_TRANSFER_DELAY = 2 days;
    uint256 public adminTransferTimestamp;

    // Emergency controls
    bool public emergencyPaused;
    address public emergencyCouncil;

    // Upgrade management
    mapping(string => address) public contractAddresses; // contract name => current implementation
    
    // Protocol parameters
    struct ProtocolParams {
        uint256 minCollateralRatio;
        uint256 devFeeMint;
        uint256 devFeeRedeem;
        uint256 endowmentFeeMint;
        bool parametersLocked;
    }
    
    ProtocolParams public params;
    
    // Events
    event Initialized(address indexed owner, address indexed emergencyCouncil);
    event AdminTransferInitiated(address indexed currentOwner, address indexed newOwner, uint256 effectiveTime);
    event AdminTransferCompleted(address indexed oldOwner, address indexed newOwner);
    event AdminTransferCancelled();
    event EmergencyPause();
    event EmergencyUnpause();
    event ParametersUpdated();
    event ParametersLocked();

    modifier onlyOwnerOrDAO() {
        require(
            msg.sender == owner() || msg.sender == governanceDAO,
            "ProtocolGovernance: caller is not owner or DAO"
        );
        _;
    }

    modifier onlyDAO() {
        require(msg.sender == governanceDAO, "ProtocolGovernance: caller is not DAO");
        _;
    }

    modifier onlyEmergencyCouncil() {
        require(msg.sender == emergencyCouncil, "ProtocolGovernance: caller is not emergency council");
        _;
    }
    
    modifier whenNotEmergencyPaused() {
        require(!emergencyPaused, "ProtocolGovernance: protocol is emergency paused");
        _;
    }
    
    modifier parametersNotLocked() {
        require(!params.parametersLocked, "ProtocolGovernance: parameters are locked");
        _;
    }

    function initialize(
        address initialOwner,
        address _emergencyCouncil
    ) external initializer {
        require(initialOwner != address(0), "ProtocolGovernance: owner is zero address");
        require(_emergencyCouncil != address(0), "ProtocolGovernance: emergencyCouncil is zero address");
        
        __Ownable_init(initialOwner);
        admin = initialOwner;
        emergencyCouncil = _emergencyCouncil;
        
        // Initialize default parameters
        params = ProtocolParams({
            minCollateralRatio: 1.10e18, // 110%
            devFeeMint: 0.01e18, // 1%
            devFeeRedeem: 0.001e18, // 0.1%
            endowmentFeeMint: 0.001e18, // 0.1%
            parametersLocked: false
        });
        
        emit Initialized(initialOwner, _emergencyCouncil);
    }
    
    function initializeContracts(
        address _btc1usd,
        address _vault,
        address _weeklyDistribution,
        address _endowmentManager,
        address _priceOracle
    ) external onlyOwner {
        require(address(btc1usd) == address(0), "ProtocolGovernance: contracts already initialized");
        
        btc1usd = IBTC1USD(_btc1usd);
        vault = IVault(_vault);
        weeklyDistribution = IWeeklyDistribution(_weeklyDistribution);
        endowmentManager = IEndowmentManager(_endowmentManager);
        priceOracle = IPriceOracle(_priceOracle);
    }
    
    // Admin transfer with time delay for security
    function initiateAdminTransfer(address newAdmin) external onlyOwner {
        require(newAdmin != address(0), "ProtocolGovernance: new admin is zero address");
        require(newAdmin != admin, "ProtocolGovernance: new admin is current admin");
        
        pendingAdmin = newAdmin;
        adminTransferTimestamp = block.timestamp.add(ADMIN_TRANSFER_DELAY);
        
        emit AdminTransferInitiated(admin, newAdmin, adminTransferTimestamp);
    }
    
    function completeAdminTransfer() external {
        require(msg.sender == pendingAdmin, "ProtocolGovernance: caller is not pending admin");
        require(block.timestamp >= adminTransferTimestamp, "ProtocolGovernance: transfer delay not met");
        require(pendingAdmin != address(0), "ProtocolGovernance: no pending admin");
        
        address oldAdmin = admin;
        admin = pendingAdmin;
        pendingAdmin = address(0);
        adminTransferTimestamp = 0;
        
        emit AdminTransferCompleted(oldAdmin, admin);
    }
    
    function cancelAdminTransfer() external onlyOwner {
        pendingAdmin = address(0);
        adminTransferTimestamp = 0;
        emit AdminTransferCancelled();
    }

    // Simple admin transfer for initial deployment (no time delay)
    function setAdmin(address _admin) external onlyOwner {
        require(_admin != address(0), "ProtocolGovernance: admin is zero address");
        admin = _admin;
    }

    // Emergency controls
    function emergencyPause() external onlyEmergencyCouncil {
        emergencyPaused = true;
        
        // Pause vault
        vault.pause();
        
        emit EmergencyPause();
    }
    
    function emergencyUnpause() external onlyOwner {
        emergencyPaused = false;
        
        // Unpause vault
        vault.unpause();
        
        emit EmergencyUnpause();
    }
    
    // Parameter management
    function updateMinCollateralRatio(uint256 newRatio) external onlyOwner parametersNotLocked {
        require(newRatio >= 1.05e18, "ProtocolGovernance: ratio too low"); // Minimum 105%
        require(newRatio <= 1.50e18, "ProtocolGovernance: ratio too high"); // Maximum 150%
        
        params.minCollateralRatio = newRatio;
        emit ParametersUpdated();
    }
    
    function updateDevFees(uint256 mintFee, uint256 redeemFee) external onlyOwner parametersNotLocked {
        require(mintFee <= 0.05e18, "ProtocolGovernance: mint fee too high"); // Max 5%
        require(redeemFee <= 0.01e18, "ProtocolGovernance: redeem fee too high"); // Max 1%
        
        params.devFeeMint = mintFee;
        params.devFeeRedeem = redeemFee;
        emit ParametersUpdated();
    }
    
    function updateEndowmentFee(uint256 fee) external onlyOwner parametersNotLocked {
        require(fee <= 0.01e18, "ProtocolGovernance: endowment fee too high"); // Max 1%
        
        params.endowmentFeeMint = fee;
        emit ParametersUpdated();
    }
    
    function lockParameters() external onlyOwner {
        params.parametersLocked = true;
        emit ParametersLocked();
    }
    
    // Contract management
    function addCollateralToken(address token) external onlyOwner whenNotEmergencyPaused {
        vault.addCollateral(token);
    }
    
    function removeCollateralToken(address token) external onlyOwner {
        vault.removeCollateral(token);
    }
    
    function updatePriceOracle(address newOracle) external onlyOwnerOrDAO {
        priceOracle = IPriceOracle(newOracle);
        contractAddresses["PriceOracle"] = newOracle;
    }

    function setEmergencyCouncil(address newCouncil) external onlyOwnerOrDAO {
        emergencyCouncil = newCouncil;
    }

    // DAO Integration Functions
    function setGovernanceDAO(address _governanceDAO) external onlyOwner {
        require(_governanceDAO != address(0), "ProtocolGovernance: DAO is zero address");
        governanceDAO = _governanceDAO;
    }

    // Contract Upgrade Functions (DAO-controlled)
    function upgradeVault(address newVault) external onlyDAO {
        require(newVault != address(0), "ProtocolGovernance: new vault is zero address");
        vault = IVault(newVault);
        contractAddresses["Vault"] = newVault;
    }

    function upgradeWeeklyDistribution(address newDistribution) external onlyDAO {
        require(newDistribution != address(0), "ProtocolGovernance: new distribution is zero address");
        weeklyDistribution = IWeeklyDistribution(newDistribution);
        contractAddresses["WeeklyDistribution"] = newDistribution;
    }

    function upgradeEndowmentManager(address newEndowment) external onlyDAO {
        require(newEndowment != address(0), "ProtocolGovernance: new endowment is zero address");
        endowmentManager = IEndowmentManager(newEndowment);
        contractAddresses["EndowmentManager"] = newEndowment;
    }

    function upgradeBTC1USD(address newToken) external onlyDAO {
        require(newToken != address(0), "ProtocolGovernance: new token is zero address");
        btc1usd = IBTC1USD(newToken);
        contractAddresses["BTC1USD"] = newToken;
    }

    // Parameter updates accessible by DAO
    function updateMinCollateralRatioDAO(uint256 newRatio) external onlyDAO parametersNotLocked {
        require(newRatio >= 1.05e18, "ProtocolGovernance: ratio too low");
        require(newRatio <= 1.50e18, "ProtocolGovernance: ratio too high");

        params.minCollateralRatio = newRatio;
        emit ParametersUpdated();
    }

    function updateDevFeeMint(uint256 newFee) external onlyDAO parametersNotLocked {
        require(newFee <= 0.05e18, "ProtocolGovernance: fee too high");

        params.devFeeMint = newFee;
        emit ParametersUpdated();
    }

    function updateDevFeeRedeem(uint256 newFee) external onlyDAO parametersNotLocked {
        require(newFee <= 0.01e18, "ProtocolGovernance: fee too high");

        params.devFeeRedeem = newFee;
        emit ParametersUpdated();
    }

    // NEW: Allow upgrading contract references
    function setBTC1USD(address _btc1usd) external onlyOwner {
        require(_btc1usd != address(0), "ProtocolGovernance: btc1usd is zero address");
        btc1usd = IBTC1USD(_btc1usd);
    }

    function setVault(address _vault) external onlyOwner {
        require(_vault != address(0), "ProtocolGovernance: vault is zero address");
        vault = IVault(_vault);
    }

    function setWeeklyDistribution(address _weeklyDistribution) external onlyOwner {
        require(_weeklyDistribution != address(0), "ProtocolGovernance: weeklyDistribution is zero address");
        weeklyDistribution = IWeeklyDistribution(_weeklyDistribution);
    }

    function setEndowmentManager(address _endowmentManager) external onlyOwner {
        require(_endowmentManager != address(0), "ProtocolGovernance: endowmentManager is zero address");
        endowmentManager = IEndowmentManager(_endowmentManager);
    }

    function setPriceOracle(address _priceOracle) external onlyOwner {
        require(_priceOracle != address(0), "ProtocolGovernance: priceOracle is zero address");
        priceOracle = IPriceOracle(_priceOracle);
    }
    
    // View functions
    function getProtocolStatus() external view returns (
        bool isHealthy,
        uint256 collateralRatio,
        uint256 totalSupply,
        uint256 totalCollateralValue,
        bool isPaused
    ) {
        isHealthy = vault.isHealthy();
        collateralRatio = vault.getCurrentCollateralRatio();
        totalSupply = btc1usd.totalSupply();
        totalCollateralValue = vault.getTotalCollateralValue();
        isPaused = emergencyPaused;
    }
    
    function getProtocolParams() external view returns (ProtocolParams memory) {
        return params;
    }
}

contracts/UpgradeableProxy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title UpgradeableProxy
 * @notice Simple transparent upgradeable proxy for protocol contracts
 * @dev Uses delegate call to forward all calls to implementation contract
 */
contract UpgradeableProxy {
    // Storage slot for implementation address
    bytes32 private constant IMPLEMENTATION_SLOT = keccak256("eip1967.proxy.implementation");

    // Storage slot for admin address
    bytes32 private constant ADMIN_SLOT = keccak256("eip1967.proxy.admin");

    event Upgraded(address indexed implementation);
    event AdminChanged(address indexed previousAdmin, address indexed newAdmin);

    /**
     * @dev Initializes the proxy with initial implementation and admin
     */
    constructor(address implementation_, address admin_) {
        _setImplementation(implementation_);
        _setAdmin(admin_);
    }

    /**
     * @dev Fallback function that delegates calls to the implementation
     */
    fallback() external payable {
        _delegate(_implementation());
    }

    receive() external payable {
        _delegate(_implementation());
    }

    /**
     * @dev Returns the current implementation address
     */
    function implementation() public view returns (address impl) {
        bytes32 slot = IMPLEMENTATION_SLOT;
        assembly {
            impl := sload(slot)
        }
    }

    /**
     * @dev Returns the current admin address
     */
    function admin() public view returns (address adm) {
        bytes32 slot = ADMIN_SLOT;
        assembly {
            adm := sload(slot)
        }
    }

    /**
     * @dev Upgrades the implementation
     * Can only be called by admin
     */
    function upgradeTo(address newImplementation) external {
        require(msg.sender == admin(), "UpgradeableProxy: caller is not admin");
        require(newImplementation != address(0), "UpgradeableProxy: new implementation is zero address");
        _setImplementation(newImplementation);
        emit Upgraded(newImplementation);
    }

    /**
     * @dev Changes the admin
     * Can only be called by current admin
     */
    function changeAdmin(address newAdmin) external {
        require(msg.sender == admin(), "UpgradeableProxy: caller is not admin");
        require(newAdmin != address(0), "UpgradeableProxy: new admin is zero address");
        address previousAdmin = admin();
        _setAdmin(newAdmin);
        emit AdminChanged(previousAdmin, newAdmin);
    }

    /**
     * @dev Stores the implementation address
     */
    function _setImplementation(address newImplementation) private {
        bytes32 slot = IMPLEMENTATION_SLOT;
        assembly {
            sstore(slot, newImplementation)
        }
    }

    /**
     * @dev Stores the admin address
     */
    function _setAdmin(address newAdmin) private {
        bytes32 slot = ADMIN_SLOT;
        assembly {
            sstore(slot, newAdmin)
        }
    }

    /**
     * @dev Delegates the current call to implementation
     */
    function _delegate(address impl) private {
        assembly {
            // Copy msg.data
            calldatacopy(0, 0, calldatasize())

            // Delegate call to implementation
            let result := delegatecall(gas(), impl, 0, calldatasize(), 0, 0)

            // Copy the returned data
            returndatacopy(0, 0, returndatasize())

            switch result
            case 0 {
                // Delegatecall failed, revert
                revert(0, returndatasize())
            }
            default {
                // Delegatecall succeeded, return data
                return(0, returndatasize())
            }
        }
    }

    /**
     * @dev Returns implementation address (internal helper for fallback)
     */
    function _implementation() private view returns (address impl) {
        bytes32 slot = IMPLEMENTATION_SLOT;
        assembly {
            impl := sload(slot)
        }
    }
}

/**
 * @title ProxyAdmin
 * @notice Admin contract for managing multiple upgradeable proxies
 * @dev Designed to be controlled by GovernanceDAO
 */
contract ProxyAdmin {
    address public owner;
    address public governanceDAO;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    event GovernanceDAOUpdated(address indexed oldDAO, address indexed newDAO);

    modifier onlyOwner() {
        require(msg.sender == owner, "ProxyAdmin: caller is not owner");
        _;
    }

    modifier onlyGovernance() {
        require(msg.sender == governanceDAO || msg.sender == owner, "ProxyAdmin: caller is not governance");
        _;
    }

    constructor(address _governanceDAO) {
        owner = msg.sender;
        governanceDAO = _governanceDAO;
    }

    /**
     * @dev Upgrades a proxy to a new implementation
     */
    function upgrade(UpgradeableProxy proxy, address implementation) external onlyGovernance {
        proxy.upgradeTo(implementation);
    }

    /**
     * @dev Changes the admin of a proxy
     */
    function changeProxyAdmin(UpgradeableProxy proxy, address newAdmin) external onlyOwner {
        proxy.changeAdmin(newAdmin);
    }

    /**
     * @dev Executes an arbitrary call on a proxy contract
     * @param target The target contract to call
     * @param data The calldata to execute
     */
    function executeCall(address target, bytes memory data) external payable onlyGovernance returns (bytes memory) {
        (bool success, bytes memory returndata) = target.call{value: msg.value}(data);
        if (!success) {
            if (returndata.length > 0) {
                // Bubble up the revert reason
                assembly {
                    let returndata_size := mload(returndata)
                    revert(add(32, returndata), returndata_size)
                }
            } else {
                revert("ProxyAdmin: call reverted without reason");
            }
        }
        return returndata;
    }

    /**
     * @dev Transfers ownership of ProxyAdmin
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "ProxyAdmin: new owner is zero address");
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    /**
     * @dev Updates the governance DAO address
     */
    function setGovernanceDAO(address newDAO) external onlyOwner {
        require(newDAO != address(0), "ProxyAdmin: new DAO is zero address");
        address oldDAO = governanceDAO;
        governanceDAO = newDAO;
        emit GovernanceDAOUpdated(oldDAO, newDAO);
    }

    /**
     * @dev Gets the implementation of a proxy
     */
    function getProxyImplementation(UpgradeableProxy proxy) external view returns (address) {
        return proxy.implementation();
    }

    /**
     * @dev Gets the admin of a proxy
     */
    function getProxyAdmin(UpgradeableProxy proxy) external view returns (address) {
        return proxy.admin();
    }
}

contracts/Vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";

import "./interfaces/IBTC1USD.sol";
import "./interfaces/IPriceOracle.sol";
import "./interfaces/IPermit2.sol";

contract Vault is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable
{
    using SafeERC20 for IERC20;

    /*//////////////////////////////////////////////////////////////
                                CONSTANTS
    //////////////////////////////////////////////////////////////*/

    uint256 public constant DECIMALS = 1e8;

    uint256 public constant MIN_COLLATERAL_RATIO        = 1.20e8;
    uint256 public constant MIN_COLLATERAL_RATIO_STABLE = 1.10e8;
    uint256 public constant STRESS_REDEMPTION_FACTOR    = 0.90e8;

    uint256 public constant DEV_FEE_MINT       = 0.01e8;   // 1%
    uint256 public constant DEV_FEE_REDEEM     = 0.001e8; // 0.1%
    uint256 public constant ENDOWMENT_FEE_MINT = 0.01e8;  // 1%

    address public constant PERMIT2 =
        0x000000000022D473030F116dDEE9F6B43aC78BA3;

    /*//////////////////////////////////////////////////////////////
                                STATE
    //////////////////////////////////////////////////////////////*/

    IBTC1USD public btc1usd;
    IPriceOracle public oracle;

    address[] public collateralTokens;
    mapping(address => bool) public supportedCollateral;
    mapping(address => uint256) public collateralBalances;

    address public devWallet;
    address public endowmentWallet;
    bool public paused;

    /*//////////////////////////////////////////////////////////////
                                EVENTS
    //////////////////////////////////////////////////////////////*/

    event Mint(address indexed user, address collateral, uint256 amountIn, uint256 btc1Out);
    event Redeem(address indexed user, address collateral, uint256 btc1In, uint256 collateralOut);
    event CollateralAdded(address token);
    event CollateralRemoved(address token);
    event Paused();
    event Unpaused();

    /*//////////////////////////////////////////////////////////////
                                MODIFIERS
    //////////////////////////////////////////////////////////////*/

    modifier whenNotPaused() {
        require(!paused, "paused");
        _;
    }

    modifier validCollateral(address token) {
        require(supportedCollateral[token], "invalid collateral");
        _;
    }

    /*//////////////////////////////////////////////////////////////
                                INIT
    //////////////////////////////////////////////////////////////*/

    function initialize(
        address owner_,
        address btc1_,
        address oracle_,
        address dev_,
        address endowment_
    ) external initializer {
        require(
            owner_ != address(0) &&
            btc1_ != address(0) &&
            oracle_ != address(0) &&
            dev_ != address(0) &&
            endowment_ != address(0),
            "zero address"
        );

        __Ownable_init(owner_);
        __ReentrancyGuard_init();

        btc1usd = IBTC1USD(btc1_);
        oracle = IPriceOracle(oracle_);
        devWallet = dev_;
        endowmentWallet = endowment_;
    }

    /*//////////////////////////////////////////////////////////////
                                ADMIN
    //////////////////////////////////////////////////////////////*/

    function addCollateral(address token) external onlyOwner {
        require(token != address(0), "zero token");
        require(!supportedCollateral[token], "already added");

        supportedCollateral[token] = true;
        collateralTokens.push(token);

        emit CollateralAdded(token);
    }

    function removeCollateral(address token) external onlyOwner {
        require(collateralBalances[token] == 0, "accounting balance not zero");
        require(IERC20(token).balanceOf(address(this)) == 0, "actual balance not zero");

        supportedCollateral[token] = false;

        // FIX: swap & pop to prevent array bloat
        uint256 len = collateralTokens.length;
        for (uint256 i = 0; i < len; i++) {
            if (collateralTokens[i] == token) {
                collateralTokens[i] = collateralTokens[len - 1];
                collateralTokens.pop();
                break;
            }
        }

        emit CollateralRemoved(token);
    }

    function pause() external onlyOwner {
        paused = true;
        emit Paused();
    }

    function unpause() external onlyOwner {
        paused = false;
        emit Unpaused();
    }

    /*//////////////////////////////////////////////////////////////
                        INTERNAL HELPERS
    //////////////////////////////////////////////////////////////*/

    function _getTotalCollateralValueInternal() internal view returns (uint256 totalValue) {
        for (uint256 i = 0; i < collateralTokens.length; i++) {
            address token = collateralTokens[i];
            uint256 bal = collateralBalances[token];
            if (bal == 0) continue;

            uint256 price = oracle.getPrice(token);
            require(price > 0, "oracle price zero");

            uint8 dec = IERC20Metadata(token).decimals();
            totalValue += bal * price / (10 ** dec);
        }
    }

    /*//////////////////////////////////////////////////////////////
                                MINT
    //////////////////////////////////////////////////////////////*/

    function mintWithPermit2(
        address collateral,
        uint256 amount,
        IPermit2.PermitTransferFrom calldata permit,
        bytes calldata signature
    ) external whenNotPaused validCollateral(collateral) nonReentrant {
        require(amount > 0, "zero amount");
        require(!oracle.isStale(), "oracle stale");
        require(permit.permitted.token == collateral, "token mismatch");
        require(permit.permitted.amount >= amount, "permit too small");
        require(permit.deadline >= block.timestamp, "permit expired");

        // FIX: compute CR BEFORE adding new collateral
        uint256 prevUSD = _getTotalCollateralValueInternal();
        uint256 prevSupply = btc1usd.totalSupply();

        uint256 prevCR = prevSupply == 0
            ? MIN_COLLATERAL_RATIO
            : (prevUSD * DECIMALS) / prevSupply;

        uint256 mintPrice = prevCR >= MIN_COLLATERAL_RATIO
            ? prevCR
            : MIN_COLLATERAL_RATIO;

        uint256 price = oracle.getPrice(collateral);
        require(price > 0, "oracle price zero");

        uint8 dec = IERC20Metadata(collateral).decimals();
        uint256 usdValue = amount * price / (10 ** dec);
        uint256 tokensToMint = usdValue * DECIMALS / mintPrice;

        // FIX: fees added ON TOP of user mint (properly backed by collateral)
        uint256 devFee = tokensToMint * DEV_FEE_MINT / DECIMALS;
        uint256 endFee = tokensToMint * ENDOWMENT_FEE_MINT / DECIMALS;
       

        // Pull collateral AFTER calculations
        IPermit2(PERMIT2).permitTransferFrom(
            permit,
            IPermit2.SignatureTransferDetails({
                to: address(this),
                requestedAmount: amount
            }),
            msg.sender,
            signature
        );

        collateralBalances[collateral] += amount;

        btc1usd.mint(msg.sender, tokensToMint);
        if (devFee > 0) btc1usd.mint(devWallet, devFee);
        if (endFee > 0) btc1usd.mint(endowmentWallet, endFee);

        emit Mint(msg.sender, collateral, amount, tokensToMint);
    }

    /*//////////////////////////////////////////////////////////////
                                REDEEM
    //////////////////////////////////////////////////////////////*/

    function redeemWithPermit(
        uint256 btc1Amount,
        address collateral,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external whenNotPaused validCollateral(collateral) nonReentrant {
        require(btc1Amount > 0, "zero amount");

        IERC20Permit(address(btc1usd)).permit(
            msg.sender,
            address(this),
            btc1Amount,
            deadline,
            v, r, s
        );

        _redeem(msg.sender, btc1Amount, collateral);
    }

    function _redeem(address user, uint256 btc1Amount, address collateral) internal {
        require(!oracle.isStale(), "oracle stale");

        uint256 prevUSD = _getTotalCollateralValueInternal();
        uint256 prevSupply = btc1usd.totalSupply();

        uint256 prevCR = prevSupply == 0
            ? MIN_COLLATERAL_RATIO
            : (prevUSD * DECIMALS) / prevSupply;

        uint256 usdValue;
        if (prevCR >= MIN_COLLATERAL_RATIO_STABLE) {
            usdValue = btc1Amount;
        } else {
            uint256 stressPrice = prevCR * STRESS_REDEMPTION_FACTOR / DECIMALS;
            usdValue = btc1Amount * stressPrice / DECIMALS;
        }

        uint256 price = oracle.getPrice(collateral);
        require(price > 0, "oracle price zero");

        uint8 dec = IERC20Metadata(collateral).decimals();
        uint256 collateralOut = usdValue * (10 ** dec) / price;

        uint256 devFee = collateralOut * DEV_FEE_REDEEM / DECIMALS;
        uint256 sendAmount = collateralOut - devFee;

        require(collateralBalances[collateral] >= collateralOut, "insufficient collateral");

        if (prevCR >= MIN_COLLATERAL_RATIO && prevSupply > btc1Amount) {
            uint256 newUSD = prevUSD - (collateralOut * price / (10 ** dec));
            uint256 newSupply = prevSupply - btc1Amount;
            require(newUSD * DECIMALS / newSupply >= MIN_COLLATERAL_RATIO, "breaks min CR");
        }

        btc1usd.burnFrom(user, btc1Amount);
        collateralBalances[collateral] -= collateralOut;

        IERC20(collateral).safeTransfer(user, sendAmount);
        if (devFee > 0) IERC20(collateral).safeTransfer(devWallet, devFee);

        emit Redeem(user, collateral, btc1Amount, collateralOut);
    }

    /*//////////////////////////////////////////////////////////////
                            VIEW FUNCTIONS
    //////////////////////////////////////////////////////////////*/

    function getTotalCollateralValue() external view returns (uint256) {
        return _getTotalCollateralValueInternal();
    }

    function getCurrentCollateralRatio() external view returns (uint256) {
        uint256 supply = btc1usd.totalSupply();
        if (supply == 0) return 0;
        return _getTotalCollateralValueInternal() * DECIMALS / supply;
    }

    function isHealthy() external view returns (bool) {
        uint256 supply = btc1usd.totalSupply();
        if (supply == 0) return true;
        return _getTotalCollateralValueInternal() * DECIMALS / supply >= MIN_COLLATERAL_RATIO;
    }

    function getCollateralList() external view returns (address[] memory) {
        return collateralTokens;
    }
}

contracts/WeeklyDistributionUpgradeable..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IBTC1USD.sol";
import "./interfaces/IMerkleDistributor.sol";
import "./interfaces/IVault.sol";
import "./libraries/SafeMath.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/PausableUpgradeable.sol";

/**
 * @title WeeklyDistributionUpgradeable
 * @notice Upgradeable weekly distribution contract
 *
 * UPGRADEABLE DESIGN:
 * - Uses OpenZeppelin upgradeable patterns
 * - No constructor (uses initialize function)
 * - Storage layout preserved for future upgrades
 */
contract WeeklyDistributionUpgradeable is Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable {
    using SafeMath for uint256;

    // Distribution tiers (in percentage, with 8 decimals to match BTC1USD)
    uint256 public constant TIER_1_MIN = 1.12e8; // 112% (production value)
    uint256 public constant TIER_1_REWARD = 0.01e8; // 1¢
    
    uint256 public constant TIER_2_MIN = 1.22e8; // 122%
    uint256 public constant TIER_2_REWARD = 0.02e8; // 2¢
    
    uint256 public constant TIER_3_MIN = 1.32e8; // 132%
    uint256 public constant TIER_3_REWARD = 0.03e8; // 3¢
    
    uint256 public constant TIER_4_MIN = 1.42e8; // 142%
    uint256 public constant TIER_4_REWARD = 0.04e8; // 4¢
    
    uint256 public constant TIER_5_MIN = 1.52e8; // 152%
    uint256 public constant TIER_5_REWARD = 0.05e8; // 5¢
    
    uint256 public constant TIER_6_MIN = 1.62e8; // 162%
    uint256 public constant TIER_6_REWARD = 0.06e8; // 6¢
    
    uint256 public constant TIER_7_MIN = 1.72e8; // 172%
    uint256 public constant TIER_7_REWARD = 0.07e8; // 7¢
    
    uint256 public constant TIER_8_MIN = 1.82e8; // 182%
    uint256 public constant TIER_8_REWARD = 0.08e8; // 8¢
    
    uint256 public constant TIER_9_MIN = 1.92e8; // 192%
    uint256 public constant TIER_9_REWARD = 0.09e8; // 9¢
    
    uint256 public constant TIER_10_MIN = 2.02e8; // 202%
    uint256 public constant TIER_10_REWARD = 0.10e8; // 10¢ (max)

    // Protocol fees (added on top of holder rewards)
    uint256 public constant MERKL_FEE = 0.00001e8; // 0.001¢
    uint256 public constant ENDOWMENT_FEE = 0.0001e8; // 0.01¢
    uint256 public constant DEV_FEE = 0.001e8; // 0.10¢

    // Minimum collateral ratio after distribution
    uint256 public constant MIN_RATIO_AFTER_DISTRIBUTION = 1.10e8; // 110%

    IBTC1USD public btc1usd;
    IVault public vault;

    address public devWallet;
    address public endowmentWallet;
    address public merklFeeCollector;
    IMerkleDistributor public merklDistributor;

    uint256 public lastDistributionTime;

    // Daily distributions - 1 day interval
    uint256 public constant DISTRIBUTION_INTERVAL = 1 days;
    uint256 public constant CLAIM_EXPIRY = 365 days;
    
    // Protocol wallets excluded from receiving holder rewards
    mapping(address => bool) public isExcludedFromRewards;
    address[] public excludedAddresses;

    struct DistributionEvent {
        uint256 timestamp;
        uint256 collateralRatio;
        uint256 rewardPerToken;
        uint256 totalRewards;
        uint256 totalSupply;
    }

    mapping(uint256 => DistributionEvent) public distributions;
    uint256 public distributionCount;

    event WeeklyDistributionExecuted(
        uint256 indexed distributionId,
        uint256 collateralRatio,
        uint256 rewardPerToken,
        uint256 totalRewards,
        uint256 timestamp
    );

    event MerkleDistributionCreated(
        uint256 indexed distributionId,
        bytes32 merkleRoot,
        uint256 totalTokensForHolders,
        uint256 totalTokensForMerkl
    );

    event AddressExcludedFromRewards(address indexed account);
    event AddressIncludedInRewards(address indexed account);
    event Initialized(address indexed owner);

    /**
     * @dev Initialize function replaces constructor for upgradeable pattern
     */
    function initialize(
        address initialOwner,
        address _btc1usd,
        address _vault,
        address _devWallet,
        address _endowmentWallet,
        address _merklFeeCollector,
        address _merklDistributor
    ) external initializer {
        require(initialOwner != address(0), "WeeklyDistribution: owner is zero address");
        require(_btc1usd != address(0), "WeeklyDistribution: btc1usd is zero address");
        require(_vault != address(0), "WeeklyDistribution: vault is zero address");
        
        __Ownable_init(initialOwner);
        __ReentrancyGuard_init();
        __Pausable_init();
        btc1usd = IBTC1USD(_btc1usd);
        vault = IVault(_vault);
        devWallet = _devWallet;
        endowmentWallet = _endowmentWallet;
        merklFeeCollector = _merklFeeCollector;
        merklDistributor = IMerkleDistributor(_merklDistributor);
        lastDistributionTime = block.timestamp;

        // Automatically exclude protocol wallets from receiving holder rewards
        _excludeAddress(_devWallet);
        _excludeAddress(_endowmentWallet);
        _excludeAddress(_merklFeeCollector);
        _excludeAddress(_merklDistributor);
        
        emit Initialized(initialOwner);
    }

    function canDistribute() public view returns (bool) {
        // Simple 1-day interval check - distribution allowed anytime after interval passes
        return block.timestamp >= lastDistributionTime.add(DISTRIBUTION_INTERVAL);
    }

    function getRewardPerToken(uint256 collateralRatio) public pure returns (uint256) {
        if (collateralRatio >= TIER_10_MIN) return TIER_10_REWARD;
        if (collateralRatio >= TIER_9_MIN) return TIER_9_REWARD;
        if (collateralRatio >= TIER_8_MIN) return TIER_8_REWARD;
        if (collateralRatio >= TIER_7_MIN) return TIER_7_REWARD;
        if (collateralRatio >= TIER_6_MIN) return TIER_6_REWARD;
        if (collateralRatio >= TIER_5_MIN) return TIER_5_REWARD;
        if (collateralRatio >= TIER_4_MIN) return TIER_4_REWARD;
        if (collateralRatio >= TIER_3_MIN) return TIER_3_REWARD;
        if (collateralRatio >= TIER_2_MIN) return TIER_2_REWARD;
        if (collateralRatio >= TIER_1_MIN) return TIER_1_REWARD;
        return 0;
    }

    function executeDistribution() external onlyOwner nonReentrant whenNotPaused {
        require(canDistribute(), "WeeklyDistribution: cannot distribute now");

        uint256 collateralValue = vault.getTotalCollateralValue();
        uint256 totalSupply = btc1usd.totalSupply();
        require(totalSupply > 0, "WeeklyDistribution: no tokens in circulation");

        uint256 eligibleSupply = totalSupply;
        for (uint256 i = 0; i < excludedAddresses.length; i++) {
            uint256 excludedBalance = btc1usd.balanceOf(excludedAddresses[i]);
            eligibleSupply = eligibleSupply.sub(excludedBalance);
        }
        require(eligibleSupply > 0, "WeeklyDistribution: no eligible holders");

        uint256 collateralRatio = collateralValue.mul(1e8).div(totalSupply);
        require(collateralRatio >= TIER_1_MIN, "WeeklyDistribution: ratio too low for distribution");

        uint256 rewardPerToken = getRewardPerToken(collateralRatio);
        uint256 totalHolderRewards = eligibleSupply.mul(rewardPerToken).div(1e8);

        // OPTIMIZED: Calculate fees with proper rounding to ensure fairness
        // Round up to prevent fees from being underpaid
        uint256 merklFee = (totalHolderRewards.mul(MERKL_FEE).add(1e8 - 1)).div(1e8);
        uint256 endowmentFee = (totalHolderRewards.mul(ENDOWMENT_FEE).add(1e8 - 1)).div(1e8);
        uint256 devFee = (totalHolderRewards.mul(DEV_FEE).add(1e8 - 1)).div(1e8);
        
        uint256 totalNewTokens = totalHolderRewards.add(merklFee).add(endowmentFee).add(devFee);
        
        uint256 newTotalSupply = totalSupply.add(totalNewTokens);
        uint256 newCollateralRatio = collateralValue.mul(1e8).div(newTotalSupply);
        
        if (newCollateralRatio < MIN_RATIO_AFTER_DISTRIBUTION) {
            uint256 maxNewTokens = collateralValue.mul(1e8).div(MIN_RATIO_AFTER_DISTRIBUTION).sub(totalSupply);
            uint256 scaleFactor = maxNewTokens.mul(1e8).div(totalNewTokens);
            
            totalHolderRewards = totalHolderRewards.mul(scaleFactor).div(1e8);
            merklFee = merklFee.mul(scaleFactor).div(1e8);
            endowmentFee = endowmentFee.mul(scaleFactor).div(1e8);
            devFee = devFee.mul(scaleFactor).div(1e8);
            rewardPerToken = rewardPerToken.mul(scaleFactor).div(1e8);
        }
        
        btc1usd.mint(address(merklDistributor), totalHolderRewards);
        btc1usd.mint(merklFeeCollector, merklFee);
        btc1usd.mint(endowmentWallet, endowmentFee);
        btc1usd.mint(devWallet, devFee);
        
        distributionCount++;
        distributions[distributionCount] = DistributionEvent({
            timestamp: block.timestamp,
            collateralRatio: collateralRatio,
            rewardPerToken: rewardPerToken,
            totalRewards: totalHolderRewards,
            totalSupply: eligibleSupply
        });
        
        lastDistributionTime = block.timestamp;
        
        emit WeeklyDistributionExecuted(
            distributionCount,
            collateralRatio,
            rewardPerToken,
            totalHolderRewards,
            block.timestamp
        );
        
        bytes32 placeholderMerkleRoot = keccak256(abi.encodePacked(block.timestamp, distributionCount));
        try merklDistributor.startNewDistribution(placeholderMerkleRoot, totalHolderRewards) {
            emit MerkleDistributionCreated(distributionCount, placeholderMerkleRoot, totalHolderRewards, merklFee);
        } catch Error(string memory reason) {
            revert(string(abi.encodePacked("WeeklyDistribution: Failed to start merkle distribution: ", reason)));
        }
    }

    function updateMerkleRoot(bytes32 merkleRoot, uint256 totalTokensForHolders) external onlyOwner {
        require(distributionCount > 0, "WeeklyDistribution: No distribution executed yet");
        require(merkleRoot != bytes32(0), "WeeklyDistribution: Invalid merkle root");

        DistributionEvent memory latestDistribution = distributions[distributionCount];
        uint256 merklFee = latestDistribution.totalSupply.mul(MERKL_FEE).div(1e8);

        merklDistributor.updateMerkleRoot(distributionCount, merkleRoot);

        emit MerkleDistributionCreated(distributionCount, merkleRoot, totalTokensForHolders, merklFee);
    }

    function getCurrentDistributionInfo() external view returns (
        uint256 distributionId,
        uint256 rewardPerToken,
        uint256 totalSupply,
        uint256 timestamp
    ) {
        require(distributionCount > 0, "WeeklyDistribution: No distributions yet");
        DistributionEvent memory latest = distributions[distributionCount];
        return (distributionCount, latest.rewardPerToken, latest.totalSupply, latest.timestamp);
    }

    function getNextDistributionTime() external view returns (uint256) {
        return lastDistributionTime.add(DISTRIBUTION_INTERVAL);
    }

    function setMerklDistributor(address _merklDistributor) external onlyOwner {
        require(_merklDistributor != address(0), "WeeklyDistribution: merklDistributor is zero address");
        merklDistributor = IMerkleDistributor(_merklDistributor);
    }

    function setDevWallet(address _devWallet) external onlyOwner {
        require(_devWallet != address(0), "WeeklyDistribution: devWallet is zero address");
        devWallet = _devWallet;
    }

    function setEndowmentWallet(address _endowmentWallet) external onlyOwner {
        if (endowmentWallet != address(0)) {
            _includeAddress(endowmentWallet);
        }
        endowmentWallet = _endowmentWallet;
        _excludeAddress(_endowmentWallet);
    }

    function setMerklFeeCollector(address _merklFeeCollector) external onlyOwner {
        if (merklFeeCollector != address(0)) {
            _includeAddress(merklFeeCollector);
        }
        merklFeeCollector = _merklFeeCollector;
        _excludeAddress(_merklFeeCollector);
    }

    function _excludeAddress(address account) internal {
        require(account != address(0), "WeeklyDistribution: zero address");
        if (!isExcludedFromRewards[account]) {
            isExcludedFromRewards[account] = true;
            excludedAddresses.push(account);
            emit AddressExcludedFromRewards(account);
        }
    }

    function _includeAddress(address account) internal {
        if (isExcludedFromRewards[account]) {
            isExcludedFromRewards[account] = false;
            for (uint256 i = 0; i < excludedAddresses.length; i++) {
                if (excludedAddresses[i] == account) {
                    excludedAddresses[i] = excludedAddresses[excludedAddresses.length - 1];
                    excludedAddresses.pop();
                    break;
                }
            }
            emit AddressIncludedInRewards(account);
        }
    }

    function excludeAddress(address account) external onlyOwner {
        _excludeAddress(account);
    }

    function includeAddress(address account) external onlyOwner {
        _includeAddress(account);
    }


    function getExcludedAddresses() external view returns (address[] memory) {
        return excludedAddresses;
    }

    function isExcluded(address account) external view returns (bool) {
        return isExcludedFromRewards[account];
    }

    // NEW: Allow upgrading vault reference
    function setVault(address _vault) external onlyOwner {
        require(_vault != address(0), "WeeklyDistribution: vault is zero address");
        vault = IVault(_vault);
    }

    // NEW: Allow upgrading BTC1USD reference
    function setBTC1USD(address _btc1usd) external onlyOwner {
        require(_btc1usd != address(0), "WeeklyDistribution: btc1usd is zero address");
        btc1usd = IBTC1USD(_btc1usd);
    }

    // Emergency pause/unpause functions
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}
